DROP TABLE IF EXISTS "teacher";
CREATE TABLE "teacher" ("teacher_id" INTEGER PRIMARY KEY  AUTOINCREMENT  NOT NULL  UNIQUE , "teacher_photo_id" TEXT UNIQUE , "first_name" TEXT NOT NULL , "middle_name" TEXT, "last_name" TEXT, "address" TEXT, "dob" DATETIME, "place_of_birth" TEXT, "religion" TEXT, "other_religion" TEXT, "email_id" TEXT, "emergency_contact_person" TEXT, "emergency_contact_number" INTEGER, "educational_background" TEXT, "languages" TEXT, "hobbies" TEXT, "place" TEXT, "date" DATETIME);
